public class ViewModel  
 {
      public List<Person> Data { get; set; }

      public ViewModel()
       {
            Data = new List<Person>()
            {
                new Person { Name = "David", Height = 180 },
                new Person { Name = "Michael", Height = 170 },
                new Person { Name = "Steve", Height = 160 },
                new Person { Name = "Joel", Height = 182 }
            };
        }
  }

  public class Person   
  {   
  public string Name { get; set; }
  public double Height { get; set; }
  }

member.example:
This sample shows how to set the Title for the chart

void MyPage()
{
	SfChart chart = new SfChart();
	chart.Title = new ChartTitle(){ Text = "My Header" };
}

This sample code shows how to set the ColorModel property of the chart. 

void MyPage()
{
	SfChart chart = new SfChart();
	chart.ColorModel.Palette = ChartColorPalette.Metro;

	this.content = chart;
}

