void MyPage()
{
	SfChart chart = new SfChart();

	CategoryAxis primary = new CategoryAxis();
	primary.Name = "Primary";
	primary.CrossesAt = 50;
	primary.RenderNextToCrossingValue = false;
	chart.PrimaryAxis = primary;

	NumericalAxis secondary = new NumericalAxis();
	secondary.Name = "Secondary";
	secondary.CrossesAt = 4;
	secondary.RenderNextToCrossingValue = false;
	chart.SecondaryAxis = secondary;
}

